import React, { useState, useRef, useEffect, useCallback } from 'react';
import { Editor } from '@monaco-editor/react';
import io from 'socket.io-client';
import { useDrag, useDrop, DndProvider, HTML5Backend } from 'react-dnd';
import { Resizable } from 'react-resizable';

function App() {
  const [consoleContent, setConsoleContent] = useState('');
  const editorRef = useRef(null);
  const socket = useRef(null);
  const [isExecuting, setIsExecuting] = useState(false);

  useEffect(() => {
    return () => {
      if (socket.current) {
        socket.current.disconnect();
      }
    };
  }, []);

  const handleExecute = useCallback(async () => {
    setIsExecuting(true);
    const content = editorRef.current.getValue();
    try {
      const response = await fetch("http://localhost:3001/compile", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ code: content }),
      });
      const data = await response.json();
      if (data && data.containerPath) {
        socket.current = io(`http://localhost:${data.containerPath}`);
        socket.current.on('s2c_message', (msg) => {
          setConsoleContent(msg.value);
          if (socket.current) {
            socket.current.disconnect();
            socket.current = null;
          }
        });
        socket.current.emit('c2s_message', { value: content });
      } else {
        console.error("Could not retrieve the container path");
      }
    } catch (error) {
      console.error("Error:", error);
    }
    setTimeout(() => setIsExecuting(false), 500);
  }, []);

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ width: '70%', margin: 'auto', display: 'flex', flexDirection: 'row' }}>
        <Resizable>
          <div style={{ flex: 1 }}>
            {/* Left Panel Contents */}
          </div>
        </Resizable>
        <Resizable>
          <div style={{ flex: 1 }}>
            <button onClick={handleExecute} disabled={isExecuting}>Execute</button>
            <Editor 
              height='500px' 
              theme='vs-dark' 
              language='javascript' 
              onMount={(editor) => editorRef.current = editor} 
            />
            <div style={{ marginTop: '20px', border: '1px solid #444', height: '200px', overflow: 'auto', backgroundColor: '#111', color: '#eee', padding: '10px' }}>
              <pre>{consoleContent}</pre>
            </div>
          </div>
        </Resizable>
      </div>
    </DndProvider>
  );
}

export default App;
