import { HTML5Backend } from 'react-dnd-html5-backend';
import { DndProvider } from 'react-dnd';
import { Resizable } from 'react-resizable';
import React, { useState, useRef, useEffect, useCallback } from 'react';
import io from 'socket.io-client';
import './App.css';
import CodeEditor from './components/CodeEditor';
import ConsoleOutput from './components/ConsoleOutput';
import ControlPanel from './components/ControlPanel';

function App() {
    const [consoleContent, setConsoleContent] = useState('');
    const socket = useRef(null);
    const [isExecuting, setIsExecuting] = useState(false);

    useEffect(() => {
        return () => {
            if (socket.current) {
                socket.current.disconnect();
            }
        };
    }, []);

    const handleExecute = useCallback(async (content) => {
        setIsExecuting(true);
        try {
            const response = await fetch("http://localhost:3001/compile", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ code: content }),
            });
            const data = await response.json();

            if (data && data.containerPath) {
                socket.current = io(`http://localhost:${data.containerPath}`);
                socket.current.on('s2c_message', (msg) => {
                    setConsoleContent(msg.value);
                    if (socket.current) {
                        socket.current.disconnect();
                        socket.current = null;
                    }
                });
                socket.current.emit('c2s_message', { value: content });
            } else {
                console.error("Could not retrieve the container path");
            }
        } catch (error) {
            console.error("Error:", error);
        }
        setTimeout(() => setIsExecuting(false), 500);
    }, []);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="container" style={{ display: 'flex' }}>
                <Resizable
                    className="left-panel"
                    width={200}
                    height={Infinity}
                    axis="x"
                    onResizeStop={(e, { size }) => { /* Update right-panel width */ }}
                >
                    <h1>2667. Create Hello World Function</h1>
                    <p>Write a function createHelloWorld...</p>
                    {/* ... Rest of the challenge content ... */}
                </Resizable>
                <div className="right-panel" style={{ flex: 1, position: 'relative' }}>
                    <ControlPanel onExecute={handleExecute} isExecuting={isExecuting} />
                    <CodeEditor onExecute={handleExecute} />
                    <ConsoleOutput content={consoleContent} />
                </div>
            </div>
        </DndProvider>
    );
}

// Define your draggable and resizable components
function ResizableDraggableControlPanel(props) {
    // Implement the dragging and resizing functionality
    // ...
    return <ControlPanel {...props} />;
}

function ResizableDraggableCodeEditor(props) {
    // Implement the dragging and resizing functionality
    // ...
    return <CodeEditor {...props} />;
}

function ResizableDraggableConsoleOutput(props) {
    // Implement the dragging and resizing functionality
    // ...
    return <ConsoleOutput {...props} />;
}

export default App;
