import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import CodingInterface from './CodingInterface.jsx';

function App() {
  const [selectedSandbox, setSelectedSandbox] = useState('javascript'); // Default sandbox

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <button onClick={() => setSelectedSandbox('javascript')}>JavaScript Sandbox</button>
            </li>
            <li>
              <button onClick={() => setSelectedSandbox('react')}>React Sandbox</button>
            </li>
          </ul>
        </nav>

        <Routes>
          {/* Passing the selectedSandbox as a prop to CodingInterface */}
          <Route path="/" element={<CodingInterface sandbox={selectedSandbox} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
