import React, { useState, useRef, useEffect, useCallback } from "react";
import { Editor } from "@monaco-editor/react";

import io from "socket.io-client";
import './EditorLayout.css';

function App() {
  // State for console content
  const [consoleContent, setConsoleContent] = useState("");

  // Ref for the Monaco Editor
  const editorRef = useRef(null);
  const editorContainer1 = useRef(null);
  const editorContainer2 = useRef(null); 

  // Ref for the socket.io connection
  const socket = useRef(null);

  // New state for tracking if execution is ongoing
  const [isExecuting, setIsExecuting] = useState(false);

  // Effect to disconnect socket.io when the component unmounts
  useEffect(() => {
    return () => {
      if (socket.current) {
        socket.current.disconnect();
      }

      if(socket.current){
        socket.current.disconnect();
      }

      if(editorContainer1.current && editorContainer2.current){
        monaco.editor.create(editorContainer1.current,{
          value:`// Code here`,
          language:'javascript'
        });

        monaco.editor.create(editorContainer2.current,{
          value: `Code here`,
          language: 'javascript'
        })
      }

    };
  }, []);

  const handleExecute = useCallback(async () => {
    setIsExecuting(true); // Set executing to true at the start

    const content = editorRef.current.getValue();

    try {
      const response = await fetch("http://localhost:3001/compile", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ code: content }),
      });
      const data = await response.json();

      if (data && data.containerPath) {
        socket.current = io(`http://localhost:${data.containerPath}`);
        socket.current.on("s2c_message", (msg) => {
          setConsoleContent(msg.value);

          if (socket.current) {
            socket.current.disconnect();
            socket.current = null; // Clear the reference
          }
        });
        socket.current.emit("c2s_message", { value: content });
      } else {
        console.error("Could not retrieve the container path");
      }
    } catch (error) {
      console.error("Error:", error);
    }

    setTimeout(() => setIsExecuting(false), 500); // Reset executing to false after 0.5 seconds
  }, []); // empty dependency array since there are no dependencies

  return (
    <div>
      <div style={{ width: "70%", margin: "auto" }}>
        <button onClick={handleExecute} disabled={isExecuting}>
          Execute
        </button>
        {/* Monaco Editor */}
        <Editor
          height="500px"
          theme="vs-dark"
          language="javascript"
          onMount={(editor) => (editorRef.current = editor)}
        />
        {/* Console Output Area */}
        <div
          style={{
            marginTop: "20px",
            border: "1px solid #444",
            height: "200px",
            overflow: "auto",
            backgroundColor: "#111",
            color: "#eee",
            padding: "10px",
          }}
        >
          <pre>{consoleContent}</pre>
        </div>
      </div>

      <div className="editor-layout">
        <div className="editor-left">
          <h2>2667.Create Hello World Function</h2>
          <p>Write a function createHelloWorld</p>
          <div className="editor-example">
            <h3>Example 1:</h3>
          </div>
          <div className="editor-constraints">
          </div>
        </div>
        <div className="editor-right">
          <div ref={editorContainer1} className="monaco-editor"></div>
          <div ref={editorContainer2} className="monaco-editor"></div>
        </div>
      </div>
    </div>
  );
}

export default App;
