import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useDrag } from 'react-dnd';
import { Resizable } from 'react-resizable';
import { Editor } from '@monaco-editor/react';

function CodeEditor({ onExecute }) {
    const editorRef = useRef(null);

    // Set up the drag source
    const [, ref] = useDrag(() => ({
        type: 'CodeEditor',
    }));

    const handleExecute = () => {
        if (editorRef.current) {
            onExecute(editorRef.current.getValue());
        }
    };

    return (
        <div ref={ref} style={{ position: 'absolute' }}>
            <Resizable
                width={400}  // initial width
                height={500}  // initial height
                onResize={(event, { element, size }) => {
                    // Update the dimensions of the editor and the container
                    element.style.width = `${size.width}px`;
                    element.style.height = `${size.height}px`;
                    editorRef.current.layout();
                }}
            >
                <div>
                    <Editor 
                        height="100%" 
                        width="100%"
                        theme='vs-dark' 
                        language='javascript' 
                        onMount={(editor) => editorRef.current = editor} 
                    />
                    <button onClick={handleExecute}>Run</button>
                </div>
            </Resizable>
        </div>
    );
}

export default CodeEditor;
