import React, { useState, useRef } from 'react';
import { Editor } from '@monaco-editor/react';
import './CodeEditor.css';

function CodeEditor({ onExecute }) {
    const [activeTab, setActiveTab] = useState('script.js');
    const [codeContent, setCodeContent] = useState('');

    const editorRef = useRef(null);
    const codeRefs = {
        'script.js': useRef(''),
        'animal.js': useRef(''),
        'dog.js': useRef('')
    };

    const handleTabChange = (tab) => {
        if (editorRef.current) {
            codeRefs[activeTab].current = editorRef.current.getValue();
        }
        setActiveTab(tab);
        setCodeContent(codeRefs[tab].current || '');
    };

    const handleExecute = () => {
        if (editorRef.current) {
            onExecute(editorRef.current.getValue());
        }
    };

    return (
        <div>
            <div className="tabs">
                {Object.keys(codeRefs).map(tab => (
                    <button 
                        key={tab} 
                        className={activeTab === tab ? 'active' : ''} 
                        onClick={() => handleTabChange(tab)}
                    >
                        {tab}
                    </button>
                ))}
                <button 
                    style={{ 
                        marginLeft: 'auto', 
                        backgroundColor: '#A7D397', 
                        color: '#333',
                        fontWeight: 'bold',
                        border: 'none',
                        padding: '5px 15px',
                        borderRadius: '5px'
                    }}
                    onClick={handleExecute}
                >
                    ▶️ Run
                </button>
            </div>
            <Editor 
                value={codeContent}
                height="500px" 
                theme="vs-dark" 
                language="javascript" 
                onMount={(editor) => editorRef.current = editor} 
            />
        </div>

    );
}

export default CodeEditor;
